import React, { useEffect, useState } from "react";
import s from "./HeroesList.module.css";

type Hero = {
    name: string;
    superpower: string;
    humility: number;
};

// Layout constants
const heroSize = 150;           // Each hero card is 150x150px
const verticalSpacing = 160;    // Vertical slot for each hero (150px card + 10px gap)
const totalZones = 10;          // There are 10 horizontal zones

function HeroesList() {
    const [heroes, setHeroes] = useState<Hero[]>([]);
    const [error, setError] = useState<string | null>(null);


    useEffect(() => {
        fetch(`${process.env.REACT_APP_BACK_END_API}/superheroes`)
            .then((res) => {
                if (!res.ok) throw new Error(`Error: ${res.status}`);
                return res.json();
            })
            .then((data) => setHeroes(data.superheroesData)) // Assumes data.superheroesData is an array
            .catch((err) => {
                console.error(err);
                setError("Failed to load superheroes.");
            });
    }, []);

    // Shuffle heroes so the vertical order is randomized
    const shuffledHeroes = [...heroes].sort(() => Math.random() - 0.5);

    // Compute horizontal zones
    const containerWidth = window.innerWidth - 10; // Approximate available width
    const zoneWidth = containerWidth / totalZones;

    return (
        <div className={s.heroesList}>
            {error && <p>{error}</p>}
            {/* Set the inner container's height to fit all heroes vertically */}
            <div
                style={{
                    position: "relative",
                    height: `${shuffledHeroes.length * verticalSpacing}px`,
                }}
            >
                {shuffledHeroes.map((hero, index) => {
                    // Calculate horizontal position based on hero's humility zone.
                    const zoneIndex = hero.humility - 1; // Convert 1-indexed humility to 0-indexed zone
                    const zoneStart = zoneIndex * zoneWidth;
                    // Random left position within the zone, ensuring the card fits.
                    const left = zoneStart + Math.random() * Math.max(0, zoneWidth - heroSize);

                    // Use the hero's slot based on its shuffled index.
                    const top = index * verticalSpacing;

                    return (
                        <div
                            key={index}
                            className={s.heroItem}
                            style={{
                                left: `${left}px`,
                                top: `${top}px`,
                                width: `${heroSize}px`,
                                height: `${heroSize}px`,
                            }}
                        >
                            <strong>{hero.name}</strong>
                            <p>{hero.superpower}</p>
                            <p>Humility: {hero.humility}</p>
                        </div>
                    );
                })}
            </div>
        </div>
    );
}

export default HeroesList;
