import React, { useEffect, useState } from "react";
import s from "./HeroesList.module.css";

type Hero = {
    name: string;
    superpower: string;
    humility: number; // Humility score (1 to 10)
};

type Position = {
    left: number;
    top: number;
};

function HeroesList() {
    const [heroes, setHeroes] = useState<Hero[]>([]);
    const [positions, setPositions] = useState<Position[]>([]); // To store unique positions
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchHeroes = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_BACK_END_API}/superheroes`);
                if (!response.ok) {
                    throw new Error(`Error: ${response.status}`);
                }
                const data = await response.json();
                setHeroes(data.superheroesData); // Assuming API returns { superheroesData: [] }

                // Generate non-overlapping positions for the heroes
                const newPositions = generatePositions(data.superheroesData);
                setPositions(newPositions);
            } catch (err) {
                console.error("Error fetching heroes:", err);
                setError("Failed to load superheroes. Please try again later.");
            }
        };

        fetchHeroes();
    }, []);

    const calculateSize = (humility: number) => {
        const minSize = 100; // Minimum size
        const maxSize = 200; // Maximum size
        return minSize + (maxSize - minSize) * ((humility - 1) / 9); // Scale between min and max based on humility
    };

    const generatePositions = (heroes: Hero[]): Position[] => {
        const containerWidth = window.innerWidth - 200; // Full width minus padding/margins
        const containerHeight = 500; // Fixed height
        const padding = 10; // Padding between items
        const positions: Position[] = [];

        heroes.forEach((hero) => {
            const itemSize = calculateSize(hero.humility); // Get the size of the item
            let position: Position;
            let isOverlapping: boolean;

            do {
                isOverlapping = false;
                const biasFactor = 1 - hero.humility / 10; // Smaller items shift right (biasFactor is higher for smaller humility)
                position = {
                    left: Math.random() * (containerWidth * biasFactor - itemSize),
                    top: Math.random() * (containerHeight - itemSize),
                };

                // Check for overlap with existing positions
                isOverlapping = positions.some(
                    (existing) =>
                        Math.abs(existing.left - position.left) < itemSize + padding &&
                        Math.abs(existing.top - position.top) < itemSize + padding
                );
            } while (isOverlapping);

            positions.push(position);
        });

        return positions;
    };

    return (
        <div className={s.heroesList}>
            {error && <p>{error}</p>}
            {heroes.map((hero, i) => {
                const position = positions[i];
                const size = calculateSize(hero.humility); // Dynamic size based on humility
                return (
                    <div
                        key={i}
                        className={s.heroItem}
                        style={{
                            left: `${position?.left}px`,
                            top: `${position?.top}px`,
                            width: `${size}px`, // Dynamic width
                            height: `${size}px`, // Dynamic height
                        }}
                    >
                        <strong>{hero.name}</strong>
                        <p>{hero.superpower}</p>
                        <p>Humility: {hero.humility}</p>
                    </div>
                );
            })}
        </div>
    );
}

export default HeroesList;
