import React, { useEffect, useState } from "react";
import s from "./HeroesList.module.css";

type Hero = {
    name: string;
    superpower: string;
    humility: number;
};

type Position = {
    left: number;
    top: number;
};

function HeroesList() {
    const [heroes, setHeroes] = useState<Hero[]>([]);
    const [positions, setPositions] = useState<Position[]>([]); // To store unique positions
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchHeroes = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_BACK_END_API}/superheroes`);
                if (!response.ok) {
                    throw new Error(`Error: ${response.status}`);
                }
                const data = await response.json();
                setHeroes(data.superheroesData); // Assuming API returns { superheroesData: [] }

                // Generate non-overlapping positions for the heroes
                const newPositions = generatePositions(data.superheroesData.length);
                setPositions(newPositions);
            } catch (err) {
                console.error("Error fetching heroes:", err);
                setError("Failed to load superheroes. Please try again later.");
            }
        };

        fetchHeroes();
    }, []);

    const generatePositions = (count: number): Position[] => {
        const containerWidth = 100; // Full width in percentage
        const containerHeight = 500; // Fixed height in px
        const itemWidth = 150; // Fixed width of a hero item
        const itemHeight = 150; // Fixed height of a hero item
        const padding = 10; // Padding between items
        const positions: Position[] = [];

        for (let i = 0; i < count; i++) {
            let position: Position;
            let isOverlapping: boolean;

            do {
                isOverlapping = false;
                position = {
                    left: Math.random() * (containerWidth - (itemWidth / window.innerWidth) * 100),
                    top: Math.random() * (containerHeight - itemHeight),
                };

                // Check for overlap with existing positions
                isOverlapping = positions.some(
                    (existing) =>
                        Math.abs(existing.left - position.left) < (itemWidth / window.innerWidth) * 100 + padding &&
                        Math.abs(existing.top - position.top) < itemHeight + padding
                );
            } while (isOverlapping);

            positions.push(position);
        }

        return positions;
    };

    return (
        <div className={s.heroesList}>
            {error && <p>{error}</p>}
            {heroes.map((hero, i) => {
                const position = positions[i];
                return (
                    <div
                        key={i}
                        className={s.heroItem}
                        style={{
                            left: `${position?.left}%`,
                            top: `${position?.top}px`,
                        }}
                    >
                        <strong>{hero.name}</strong>
                        <p>{hero.superpower}</p>
                        <p>Humility: {hero.humility}</p>
                    </div>
                );
            })}
        </div>
    );
}

export default HeroesList;
