import s from './HeroesList.module.css';
import { useEffect, useState } from 'react';

type Hero = {
    name: string;
    superpower: string;
    humility: number; // TODO VALIDATION 10-1
};

function HeroesList() {
    const [heroes, setHeroes] = useState<Array<Hero>>([]);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchHeroes = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_BACK_END_API}/superheroes`);
                if (!response.ok) {
                    throw new Error(`Error: ${response.status}`);
                }
                const data = await response.json();
                setHeroes(data.superheroesData); // Assuming API returns { superheroesData: [] }
            } catch (err) {
                console.error("Error fetching heroes:", err);
                setError("Failed to load superheroes. Please try again later.");
            }
        };

        fetchHeroes();
    }, []);

    console.log("ENV:", process.env);

    return (
        <div className={s.heroesList}>
            <h1>Humble Heroes</h1>
            {error && <p className={s.error}>{error}</p>}
            {heroes.length > 0 ? (
                heroes.map((hero, i) => (
                    <div key={i} className={s.heroItem}>
                        <strong>{hero.name}</strong>: {hero.superpower} (Humility: {hero.humility})
                    </div>
                ))
            ) : (
                !error && <p>Loading heroes...</p>
            )}
        </div>
    );
}

export default HeroesList;
