import React, { useState } from "react";
import s from "./CreateHero.module.css";

type Hero = {
    name: string;
    superpower: string;
    humility: number;
};

function CreateHero() {
    const [name, setName] = useState("");
    const [superpower, setSuperpower] = useState("");
    const [humility, setHumility] = useState(1);

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        const newHero: Hero = { name, superpower, humility };

        
        setName("");
        setSuperpower("");
        setHumility(1);
    };

    return (
        <div className={s.createHero}>
            <h2 className={s.heading}>Create New Hero</h2>
            <form className={s.form} onSubmit={handleSubmit}>
                <div className={s.formGroup}>
                    <label htmlFor="name" className={s.label}>
                        Hero Name:
                    </label>
                    <input
                        type="text"
                        id="name"
                        className={s.input}
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div className={s.formGroup}>
                    <label htmlFor="superpower" className={s.label}>
                        Superpower:
                    </label>
                    <input
                        type="text"
                        id="superpower"
                        className={s.input}
                        value={superpower}
                        onChange={(e) => setSuperpower(e.target.value)}
                        required
                    />
                </div>
                <div className={s.formGroup}>
                    <label htmlFor="humility" className={s.label}>
                        Humility (1-10):
                    </label>
                    <input
                        type="number"
                        id="humility"
                        className={s.input}
                        value={humility}
                        onChange={(e) => setHumility(Number(e.target.value))}
                        min="1"
                        max="10"
                        required
                    />
                </div>
                <button type="submit" className={s.button}>
                    Create Hero
                </button>
            </form>
        </div>
    );
}

export default CreateHero;
