import React, { useState } from "react";
import s from "./CreateHero.module.css";

type Hero = {
    name: string;
    superpower: string;
    humility: number;
};

function CreateHero() {
    const [hero, setHero] = useState<Hero>({
        name: "",
        superpower: "",
        humility: 1,
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { id, value } = e.target;
        setHero((prev) => ({
            ...prev,
            [id]: id === "humility" ? Number(value) : value,
        }));
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        console.log("New Hero Created:", hero);
        // Optionally send hero to your API here.

        // Reset form fields.
        setHero({ name: "", superpower: "", humility: 1 });
    };

    return (
        <div className={s.container}>
            <h2>Create New Hero</h2>
            <form onSubmit={handleSubmit}>
                <div className={s.field}>
                    <label htmlFor="name">Hero Name:</label>
                    <input
                        type="text"
                        id="name"
                        value={hero.name}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className={s.field}>
                    <label htmlFor="superpower">Superpower:</label>
                    <input
                        type="text"
                        id="superpower"
                        value={hero.superpower}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className={s.field}>
                    <label htmlFor="humility">Humility (1-10):</label>
                    <input
                        type="number"
                        id="humility"
                        value={hero.humility}
                        onChange={handleChange}
                        min="1"
                        max="10"
                        required
                    />
                </div>
                <button type="submit">Create Hero</button>
            </form>
        </div>
    );
}

export default CreateHero;
