import s from "./HeroesList.module.css";
import { useEffect, useState } from "react";

type Hero = {
    name: string;
    superpower: string;
    humility: number; // TODO VALIDATION 10-1
};

function HeroesList() {
    const [heroes, setHeroes] = useState<Array<Hero>>([]);

    useEffect(() => {
        const fetchHeroes = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_BACK_END_API}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setHeroes(data.superheroesData); // Assuming API returns { superheroesData: [] }
            } catch (error) {
                console.error("Error fetching heroes:", error);
            }
        };

        fetchHeroes();
    }, []);

    console.log("API Endpoint:", process.env.REACT_APP_BACK_END_API);

    return (
        <div className={s.heroesList}>
            {heroes.length > 0 ? (
                heroes.map((hero, i) => (
                    <div key={i} className={s.heroItem}>
                        <strong>{hero.name}</strong>: {hero.superpower} (Humility: {hero.humility})
                    </div>
                ))
            ) : (
                <p>Loading heroes...</p>
            )}
        </div>
    );
}

export default HeroesList;
