import React, { useEffect, useState } from "react";
import s from "./HeroesList.module.css";

type Hero = {
    name: string;
    superpower: string;
    humility: number;
};

function HeroesList() {
    const [heroes, setHeroes] = useState<Hero[]>([]);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchHeroes = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_BACK_END_API}/superheroes`);
                if (!response.ok) {
                    throw new Error(`Error: ${response.status}`);
                }
                const data = await response.json();
                setHeroes(data.superheroesData); // Assuming API returns { superheroesData: [] }
            } catch (err) {
                console.error("Error fetching heroes:", err);
                setError("Failed to load superheroes. Please try again later.");
            }
        };

        fetchHeroes();
    }, []);

    // Generate random positions for each hero
    const getRandomPosition = (humility = 1) => {
        const containerWidth = 100; // Full width of the container in percentage
        const containerHeight = 500; // Fixed height in px

        return {
            right: `${Math.random() * (containerWidth - 15)/humility}%`, // Random horizontal position
            top: `${Math.random() * (containerHeight - 150)}px`, // Random vertical position
        };
    };

    return (
        <div className={s.heroesList}>
            {error && <p>{error}</p>}
            {heroes.map((hero, i) => {
                const randomPosition = getRandomPosition(hero.humility);
                return (
                    <div
                        key={i}
                        className={s.heroItem}
                        style={{
                            left: randomPosition.right,
                            top: randomPosition.top,
                        }}
                    >
                        <strong>{hero.name}</strong>
                        <p>{hero.superpower}</p>
                        <p>Humility: {hero.humility}</p>
                    </div>
                );
            })}
        </div>
    );
}

export default HeroesList;
